import numpy as np
def R_Square(x, y):
    p1 = x2 = y2 = 0
    x_ = np.mean(x)
    y_ = np.mean(y)
    for i in range(len(x)):
        p1 += (x[i]-x_)*(y[i]-y_)
        x2 += (x[i]-x_)**2
        y2 += (y[i]-y_)**2
    r = p1/((x2**0.5)*(y2**0.5))
    return r

def order(X, Y):
    for i in range(len(Y)):
        for j in range(i, len(Y)):
            if Y[i] > Y[j]:
                y = Y[i]
                Y[i] = Y1[j]
                Y[j] = y
                x = X[i]
                X[i] = X[j]
                X[j] = x
